$(".magnified").hover(function(e){

    // store position & dimension information of image
    var imgPos = $(".magnify").position(),
        imgHeight = $(".magnified").height(),
        imgWidth = $(".magnified").width();

    // show magnifier on hover
    $(".magnifier").show();

    // move magnifier with mouse
    $(this).mousemove(function(e){
        // store position of mouse as it moves
        var posX = e.pageX - imgPos.left,
            posY = e.pageY - imgPos.top,
            percX = (posX / imgWidth) * 100,
            percY = (posY / imgHeight) * 100,
            perc = percX + "% " + percY + "%";

        // change css of magnifier to move it to mouse
        $(".magnifier").css({
            top:posY,
            left:posX,
            backgroundPosition: perc
        });
    });
}, function(){
    // hide magnifier when not hovering
    $(".magnifier").hide();
});